('HP:0011460', 'Embryonal onset', 'Onset of disease at up to 8 weeks of gestation.'),
('HP:0011461', 'Fetal onset', 'Onset prior to birth but after 8 weeks of gestation.'),
('HP:0011462', 'Young adult onset', 'Onset of disease at the age of between 16 and 40 years.'),
('HP:0011463', 'Childhood onset', 'Onset of disease at the age of between 1 and 5 years.'),
('HP:0011464', 'Aganglionosis of the small intestine', 'A lack of intestinal ganglion cells (i.e., an aganglionic section of bowel) affecting the small intestine.'),
('HP:0011465', 'Duodenal aganglionosis', 'A lack of intestinal ganglion cells (i.e., an aganglionic section of bowel) affecting the duodenum.'),
('HP:0011466', 'Aplasia/Hypoplasia of the gallbladder', 'Absence or underdevelopment of the gallbladder.'),
('HP:0011467', 'Absent gallbladder', 'A developmental defect in which the gallbladder fails to form.'),
('HP:0011468', 'Facial tics', 'Sudden, repetitive, nonrhythmic motor movements (spasms), involving the eyes and muscles of the face.'),
('HP:0011469', 'Nasal regurgitation', 'Regurgitation of milk through the nose.'),
('HP:0011470', 'Nasogastric tube feeding in infancy', 'Feeding problem necessitating nasogastric tube feeding.'),
('HP:0011471', 'Gastrostomy tube feeding in infancy', 'Feeding problem necessitating gastrostomy tube feeding.'),
